mapper.select:
SELECT * FROM (
	SELECT ROWNUM rnum, H.* FROM (
		SELECT  SEAT_ID, OFFICE_SEQ, SEATED_DATE
		FROM    USER_SEAT_TB
		WHERE   USER_ID = #{user_id}
		ORDER BY SEATED_DATE DESC
	) H
	WHERE ROWNUM <= #{endRowNum}
)
WHERE rnum >= #{startRowNum}

SELECT * FROM (
	SELECT ROWNUM rnum, H.* FROM (
		SELECT  SEAT_ID, OFFICE_SEQ, SEATED_DATE
		FROM    USER_SEAT_TB
		WHERE   (USER_ID = #{user_id})
		AND		(SEATED_DATE BETWEEN #{from} AND #{to})
		ORDER BY SEATED_DATE DESC
	) H
	WHERE ROWNUM <= #{endRowNum}
)
WHERE rnum >= #{startRowNum}

SELECT	COUNT(*) CNT
FROM    USER_SEAT_TB
WHERE   USER_ID = #{user_id}
